

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
		for(int i=1;i<=100;i++){
			/*if(i==50){
				 continue;
			 }*/
			System.out.println("i = "+i);
			sum=sum+i;
		 System.out.println(sum);
		
		}
		
		System.out.println("final sum is "+sum);

	}

}
