package stringExample;

public class SubString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="HariGupta";
		
		s.substring(0, 4);
		// begin index- included
		// end index  - excluded
		System.out.println(s);
		
		
		s=s.substring(4);
		System.out.println(s);

	}

}
